import React, { useEffect, useState } from 'react';
import axios from 'axios';

const AdminPanel = () => {
  const [users, setUsers] = useState([]);
  const [message, setMessage] = useState({});

  // Fetch unapproved users on mount
  useEffect(() => {
    axios.get('http://localhost:5000/api/users/pending')
      .then(res => setUsers(res.data))
      .catch(err => console.error('Failed to fetch pending users:', err));
  }, []);

  const handleDecision = (userId, decision) => {
    axios.patch(`http://localhost:5000/api/users/approve/${userId}`, {
      approved: decision === 'approve',
      message: message[userId] || ''
    })
      .then(() => {
        setUsers(prev => prev.filter(u => u._id !== userId));
        alert(`User ${decision}d successfully.`);
      })
      .catch(err => console.error('Update failed:', err));
  };

  return (
    <div style={{ padding: '20px' }}>
      <h2>Admin Panel - Pending User Approvals</h2>
      {users.length === 0 ? <p>No pending users.</p> : (
        users.map(user => (
          <div key={user._id} style={{ border: '1px solid #ccc', marginBottom: '20px', padding: '10px' }}>
            <p><strong>Name:</strong> {user.firstName} {user.lastName}</p>
            <p><strong>Email:</strong> {user.email}</p>
            <p><strong>Company:</strong> {user.companyName || 'N/A'}</p>
            <textarea
              placeholder="Enter approval/rejection message"
              onChange={e => setMessage({ ...message, [user._id]: e.target.value })}
              value={message[user._id] || ''}
              style={{ width: '100%', marginBottom: '10px' }}
            />
            <button onClick={() => handleDecision(user._id, 'approve')} style={{ marginRight: '10px' }}>✅ Approve</button>
            <button onClick={() => handleDecision(user._id, 'reject')}>❌ Reject</button>
          </div>
        ))
      )}
    </div>
  );
};

export default AdminPanel;
