import React, { useEffect, useState } from "react";
import axios from "axios";
import EmailModal from "./EmailModal";
import { Table, Button } from "react-bootstrap";

const AllMembers = () => {
  const [members, setMembers] = useState([]);
  const [showModal, setShowModal] = useState(false);
  const [selectedEmail, setSelectedEmail] = useState("");

  useEffect(() => {
    const fetchMembers = async () => {
      try {
        const token = localStorage.getItem("token");
        const res = await axios.get("/api/all-members", {
          headers: { Authorization: `Bearer ${token}` },
        });
        setMembers(res.data);
      } catch (error) {
        console.error("Error fetching members:", error);
      }
    };

    fetchMembers();
  }, []);

  const handleSendEmail = (email) => {
    setSelectedEmail(email);
    setShowModal(true);
  };

  const handleEmailSubmit = async ({ to, subject, message }) => {
    try {
      const token = localStorage.getItem("token");
      await axios.post(
        "/api/all-members/send-email",
        { to, subject, message },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      alert("Email sent successfully!");
    } catch (error) {
      console.error("Error sending email:", error);
      alert("Failed to send email.");
    }
  };

  return (
    <div className="container mt-4">
      <h2>ðŸ“‹ All Approved Members</h2>
      <Table striped bordered hover>
        <thead>
          <tr>
            <th>#</th>
            <th>Full Name</th>
            <th>Email</th>
            <th>Role</th>
            <th>Send Email</th>
          </tr>
        </thead>
        <tbody>
          {members.map((member, index) => (
            <tr key={member._id}>
              <td>{index + 1}</td>
              <td>{member.firstName} {member.lastName}</td>
              <td>{member.email}</td>
              <td>{member.role}</td>
              <td>
                <Button
                  variant="outline-primary"
                  onClick={() => handleSendEmail(member.email)}
                >
                  Send Email
                </Button>
              </td>
            </tr>
          ))}
        </tbody>
      </Table>

      <EmailModal
        show={showModal}
        onHide={() => setShowModal(false)}
        recipient={selectedEmail}
        onSend={handleEmailSubmit}
      />
    </div>
  );
};

export default AllMembers;
