import React, { useEffect, useState } from "react";
import axios from "axios";
import EmailModal from "../components/EmailModal";
import { Button, Table, Badge } from "react-bootstrap";
import { FaEnvelope, FaEdit, FaTrash } from "react-icons/fa";

const AllMembers = () => {
  const [members, setMembers] = useState([]);
  const [selectedEmail, setSelectedEmail] = useState("");
  const [showModal, setShowModal] = useState(false);

  const token = localStorage.getItem("token");

  const fetchApprovedUsers = async () => {
    try {
      const res = await axios.get("/api/all-members/approved", {
        headers: { Authorization: `Bearer ${token}` },
      });
      setMembers(res.data);
    } catch (err) {
      console.error("Error fetching users", err);
    }
  };

  useEffect(() => {
    fetchApprovedUsers();
  }, []);

  const handleOpenEmail = (email) => {
    setSelectedEmail(email);
    setShowModal(true);
  };

  const handleCloseEmail = () => {
    setSelectedEmail("");
    setShowModal(false);
  };

  const handleDelete = async (userId) => {
    if (!window.confirm("Are you sure you want to delete this user?")) return;
    try {
      await axios.delete(`/api/all-members/${userId}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      alert("✅ User deleted successfully");
      fetchApprovedUsers();
    } catch (err) {
      console.error("Delete failed", err);
      alert("❌ Failed to delete user");
    }
  };

  const handleEdit = (user) => {
    alert("Edit user placeholder"); // You can replace this later with actual form/modal
  };

  return (
    <div className="container mt-4">
      <h2>
        <span role="img" aria-label="members">📝</span> All Approved Members
      </h2>

      <Table striped bordered hover responsive className="mt-4">
        <thead className="table-dark">
          <tr>
            <th>#</th>
            <th>Full Name</th>
            <th>Email</th>
            <th>Role</th>
            <th>Status</th>
            <th>Send Email</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {members.map((member, index) => (
            <tr key={member._id}>
              <td>{index + 1}</td>
              <td>{`${member.firstName} ${member.lastName}`}</td>
              <td>{member.email}</td>
              <td>
                <Badge bg="info" text="dark">{member.role}</Badge>
              </td>
              <td>
                <Badge bg="success">Approved</Badge>
              </td>
              <td>
                <Button
                  variant="outline-primary"
                  size="sm"
                  onClick={() => handleOpenEmail(member.email)}
                >
                  <FaEnvelope className="me-2" /> Send Email
                </Button>
              </td>
              <td>
                <Button
                  variant="warning"
                  size="sm"
                  className="me-2"
                  onClick={() => handleEdit(member)}
                >
                  <FaEdit className="me-1" /> Edit
                </Button>
                <Button
                  variant="danger"
                  size="sm"
                  onClick={() => handleDelete(member._id)}
                >
                  <FaTrash className="me-1" /> Delete
                </Button>
              </td>
            </tr>
          ))}
        </tbody>
      </Table>

      {/* Email Modal */}
      <EmailModal
        show={showModal}
        handleClose={handleCloseEmail}
        userEmail={selectedEmail}
      />
    </div>
  );
};

export default AllMembers;
