import React, { useState } from 'react';
import { useStytch } from '@stytch/react';

const Login = () => {
  const stytch = useStytch();
  const [email, setEmail] = useState('');

  const handleMagicLinkLogin = () => {
    if (!email) return alert('Please enter your email');
  
    stytch.magicLinks.email.loginOrCreate({
      email: email.trim(), // ✅ ensure it's a string without spaces
      login_magic_link_url: 'http://localhost:3000',
      signup_magic_link_url: 'http://localhost:3000',
    });
  };

  return (
    <div style={{ textAlign: 'center', paddingTop: '100px' }}>
      <h2>Login to Kings Portal</h2>
      <input
        type="email"
        placeholder="Enter your email"
        value={email}
        onChange={(e) => setEmail(e.target.value)}
        style={{ padding: '8px', marginRight: '10px' }}
      />
      <button onClick={handleMagicLinkLogin}>Send Magic Link</button>
    </div>
  );
};

export default Login;
