import React, { useEffect, useState } from "react";
import axios from "axios";
import { Container, Table, Form, Button } from "react-bootstrap";
import { FaTools } from "react-icons/fa";

const UpcomingMaintenance = () => {
  const [assets, setAssets] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");
  const [typeInputs, setTypeInputs] = useState({});
  const [noteInputs, setNoteInputs] = useState({});

  useEffect(() => {
    axios
      .get("/api/maintenance/upcoming-maintenance")
      .then((res) => {
        const sorted = res.data.sort(
          (a, b) => new Date(b.scheduledMaintenance) - new Date(a.scheduledMaintenance)
        );
        setAssets(sorted);
      })
      .catch((err) => console.error("Error fetching upcoming maintenance:", err));
  }, []);

  const handleSave = async (assetId, scheduledDate) => {
    const payload = {
      maintenanceType: typeInputs[assetId] || "",
      maintenanceNotes: noteInputs[assetId] || "",
      scheduledDate,
    };

    try {
      await axios.post(`/api/maintenance/save-maintenance/${assetId}`, payload);
      alert("Saved and email sent successfully!");
    } catch (error) {
      console.error("Error saving maintenance:", error);
      alert("Failed to save.");
    }
  };

  const handleDownloadCSV = () => {
    const headers = [
      "Asset Name",
      "Location",
      "Status",
      "Assigned To",
      "Maintenance Date",
      "Type",
      "Notes",
    ];
    const rows = filteredAssets.map((asset) => [
      asset.assetName,
      asset.location,
      asset.status,
      asset.assignedTo
        ? `${asset.assignedTo.firstName} ${asset.assignedTo.lastName}`
        : "Not Assigned",
      new Date(asset.scheduledMaintenance).toLocaleDateString(),
      typeInputs[asset._id] || "",
      noteInputs[asset._id] || "",
    ]);
    const csvContent = [headers, ...rows].map((e) => e.join(",")).join("\n");

    const blob = new Blob([csvContent], { type: "text/csv" });
    const href = URL.createObjectURL(blob);
    const a = document.createElement("a");
    a.href = href;
    a.download = "upcoming_maintenance.csv";
    document.body.appendChild(a);
    a.click();
    document.body.removeChild(a);
  };

  const today = new Date();
  const dueSoon = (dateStr) => {
    const target = new Date(dateStr);
    const diff = (target - today) / (1000 * 60 * 60 * 24);
    return diff >= 0 && diff <= 7;
  };

  const filteredAssets = assets.filter((asset) => {
    const query = searchTerm.toLowerCase();
    return (
      asset.assetName.toLowerCase().includes(query) ||
      asset.location.toLowerCase().includes(query) ||
      asset.status.toLowerCase().includes(query) ||
      (asset.assignedTo &&
        `${asset.assignedTo.firstName} ${asset.assignedTo.lastName}`.toLowerCase().includes(query))
    );
  });

  return (
    <Container>
      <h2 className="my-4">
        <FaTools className="me-2" />
        Upcoming Maintenance
      </h2>

      <Form.Control
        type="text"
        placeholder="Search by asset, location, status or person"
        value={searchTerm}
        onChange={(e) => setSearchTerm(e.target.value)}
        className="mb-3"
      />

      <Button variant="success" className="mb-3" onClick={handleDownloadCSV}>
        Export CSV
      </Button>

      <Table striped bordered hover responsive>
        <thead>
          <tr>
            <th>Asset Name</th>
            <th>Location</th>
            <th>Status</th>
            <th>Assigned To</th>
            <th>Maintenance Date</th>
            <th>Type</th>
            <th>Notes</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
          {filteredAssets.map((asset) => (
            <tr
              key={asset._id}
              style={{
                backgroundColor: dueSoon(asset.scheduledMaintenance) ? "#fff3cd" : "",
              }}
            >
              <td>{asset.assetName}</td>
              <td>{asset.location}</td>
              <td>{asset.status}</td>
              <td>
                {asset.assignedTo
                  ? `${asset.assignedTo.firstName} ${asset.assignedTo.lastName}`
                  : "Not Assigned"}
              </td>
              <td>{new Date(asset.scheduledMaintenance).toLocaleDateString()}</td>
              <td>
                <Form.Control
                  type="text"
                  placeholder="e.g. Inspection"
                  value={typeInputs[asset._id] || ""}
                  onChange={(e) =>
                    setTypeInputs({ ...typeInputs, [asset._id]: e.target.value })
                  }
                />
              </td>
              <td>
                <Form.Control
                  as="textarea"
                  rows={1}
                  placeholder="Write notes..."
                  value={noteInputs[asset._id] || ""}
                  onChange={(e) =>
                    setNoteInputs({ ...noteInputs, [asset._id]: e.target.value })
                  }
                />
              </td>
              <td>
                <Button
                  variant="primary"
                  onClick={() =>
                    handleSave(asset._id, asset.scheduledMaintenance)
                  }
                >
                  Save
                </Button>
              </td>
            </tr>
          ))}
        </tbody>
      </Table>
    </Container>
  );
};

export default UpcomingMaintenance;
