import React, { useEffect, useState } from 'react';
import axios from 'axios';

const AdminPanel = () => {
  const [users, setUsers] = useState([]);
  const [message, setMessage] = useState('');
  const [refresh, setRefresh] = useState(false);

  useEffect(() => {
    axios.get('/api/users')
      .then(res => setUsers(res.data))
      .catch(err => console.error('Error fetching users:', err));
  }, [refresh]);

  const handleApprove = (id) => {
    axios.patch(`/api/users/${id}/approve`)
      .then(() => {
        alert('‚úÖ User approved and notified.');
        setRefresh(!refresh);
      })
      .catch(err => console.error('Approval error:', err));
  };

  const handleReject = (id) => {
    const reason = prompt('‚ùå Enter rejection reason:');
    if (!reason) return;

    axios.patch(`/api/users/${id}/reject`, { message: reason })
      .then(() => {
        alert('‚ùå User rejected and notified.');
        setRefresh(!refresh);
      })
      .catch(err => console.error('Rejection error:', err));
  };

  const pendingUsers = users.filter(user => !user.approved);

  return (
    <div style={{ padding: '20px' }}>
      <h2>üîê Admin Panel - Pending Approvals</h2>
      {pendingUsers.length === 0 ? (
        <p>No pending users.</p>
      ) : (
        <table border="1" cellPadding="10" style={{ marginTop: '20px' }}>
          <thead>
            <tr>
              <th>Email</th>
              <th>Full Name</th>
              <th>Company</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {pendingUsers.map(user => (
              <tr key={user._id}>
                <td>{user.email}</td>
                <td>{user.firstName} {user.lastName}</td>
                <td>{user.companyName || 'N/A'}</td>
                <td>
                  <button onClick={() => handleApprove(user._id)} style={{ marginRight: '10px' }}>‚úÖ Approve</button>
                  <button onClick={() => handleReject(user._id)}>‚ùå Reject</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
};

export default AdminPanel;
