// src/pages/AllMembers.js

import React, { useEffect, useState } from "react";
import axios from "axios";
import EmailModal from "../components/EmailModal";
import { Button, Table } from "react-bootstrap";
import { FaEnvelope, FaEdit, FaTrash } from "react-icons/fa";

const AllMembers = () => {
  const [members, setMembers] = useState([]);
  const [selectedEmail, setSelectedEmail] = useState("");
  const [showModal, setShowModal] = useState(false);

  const fetchApprovedUsers = async () => {
    try {
      const res = await axios.get("/api/all-members/approved");
      setMembers(res.data);
    } catch (err) {
      console.error("Error fetching users", err);
    }
  };

  useEffect(() => {
    fetchApprovedUsers();
  }, []);

  const handleOpenEmail = (email) => {
    setSelectedEmail(email);
    setShowModal(true);
  };

  const handleCloseEmail = () => {
    setShowModal(false);
    setSelectedEmail("");
  };

  const handleDelete = async (id) => {
    if (!window.confirm("Are you sure you want to delete this user?")) return;

    try {
      await axios.delete(`/api/all-members/${id}`);
      alert("‚úÖ User deleted");
      fetchApprovedUsers(); // Refresh list
    } catch (err) {
      console.error("Delete failed", err);
      alert("‚ùå Failed to delete user");
    }
  };

  const handleEdit = (user) => {
    alert("Edit user placeholder: " + user.firstName + " " + user.lastName);
    // Future: Open edit modal
  };

  return (
    <div className="container mt-4">
      <h2>
        <span role="img" aria-label="members">üìù</span> All Approved Members
      </h2>
      <Table striped bordered hover className="mt-3">
        <thead>
          <tr>
            <th>#</th>
            <th>Full Name</th>
            <th>Email</th>
            <th>Role</th>
            <th>Status</th>
            <th>Send Email</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {members.map((member, index) => (
            <tr key={member._id}>
              <td>{index + 1}</td>
              <td>{member.firstName} {member.lastName}</td>
              <td>{member.email}</td>
              <td>
                <span className="badge bg-info text-dark">
                  {member.role}
                </span>
              </td>
              <td>
                <span className="badge bg-success">Approved</span>
              </td>
              <td>
                <Button
                  variant="outline-primary"
                  size="sm"
                  onClick={() => handleOpenEmail(member.email)}
                >
                  <FaEnvelope className="me-1" /> Send Email
                </Button>
              </td>
              <td>
                <Button
                  variant="warning"
                  size="sm"
                  className="me-2"
                  onClick={() => handleEdit(member)}
                >
                  <FaEdit className="me-1" /> Edit
                </Button>
                <Button
                  variant="danger"
                  size="sm"
                  onClick={() => handleDelete(member._id)}
                >
                  <FaTrash className="me-1" /> Delete
                </Button>
              </td>
            </tr>
          ))}
        </tbody>
      </Table>

      {/* Email Modal */}
      <EmailModal
        show={showModal}
        handleClose={handleCloseEmail}
        userEmail={selectedEmail}
      />
    </div>
  );
};

export default AllMembers;
