import React, { useState } from 'react';
import { useStytch } from '@stytch/react';

const Login = () => {
  const stytchClient = useStytch();
  const [email, setEmail] = useState('');

  const handleMagicLinkLogin = async () => {
    try {
      await stytchClient.magicLinks.email.loginOrCreate({
        email: email.trim(), // trim to remove extra spaces
        login_magic_link_url: 'http://localhost:3000', // must match allowed redirect URL in dashboard
      });
      alert('✅ Magic link sent to your email!');
    } catch (err) {
      console.error('login error:', err);
      alert(`Error: ${err.message}`);
    }
  };

  return (
    <div style={{ textAlign: 'center', marginTop: '100px' }}>
      <h2>Login to Kings Portal</h2>
      <input
        type="email"
        value={email}
        onChange={(e) => setEmail(e.target.value)}
        placeholder="Enter your email"
        style={{ padding: '10px', fontSize: '16px' }}
      />
      <button
        onClick={handleMagicLinkLogin}
        style={{ marginLeft: '10px', padding: '10px 15px' }}
      >
        Send Magic Link
      </button>
    </div>
  );
};

export default Login;
