import React, { useEffect, useState } from "react";
import axios from "axios";
import { Button, Table, Alert, Form } from "react-bootstrap";

const PendingApproval = () => {
  const [pendingUsers, setPendingUsers] = useState([]);
  const [error, setError] = useState("");
  const [success, setSuccess] = useState("");

  const fetchPendingUsers = async () => {
    try {
      const res = await axios.get("/api/users/pending", {
        headers: {
          Authorization: `Bearer ${localStorage.getItem("token")}`,
        },
      });
      setPendingUsers(res.data);
    } catch (err) {
      setError("Failed to fetch pending users.");
    }
  };

  useEffect(() => {
    fetchPendingUsers();
  }, []);

  const handleApprove = async (userId, selectedRole) => {
    try {
      if (!selectedRole) {
        setError("Please select a role before approving.");
        return;
      }

      await axios.patch(
        `/api/users/${userId}/approve`,
        { role: selectedRole },
        {
          headers: {
            Authorization: `Bearer ${localStorage.getItem("token")}`,
          },
        }
      );

      setSuccess("User approved and email sent.");
      setError("");
      fetchPendingUsers();
    } catch (err) {
      setError("Approval failed.");
      setSuccess("");
    }
  };

  const handleReject = async (userId) => {
    try {
      await axios.patch(
        `/api/users/${userId}/reject`,
        {},
        {
          headers: {
            Authorization: `Bearer ${localStorage.getItem("token")}`,
          },
        }
      );

      setSuccess("User rejected and email sent.");
      setError("");
      fetchPendingUsers();
    } catch (err) {
      setError("Rejection failed.");
      setSuccess("");
    }
  };

  return (
    <div className="container mt-4">
      <h3>ðŸš¦ Pending Approvals</h3>

      {error && <Alert variant="danger">{error}</Alert>}
      {success && <Alert variant="success">{success}</Alert>}

      <Table striped bordered hover responsive className="mt-3">
        <thead>
          <tr>
            <th>Email</th>
            <th>Phone</th>
            <th>Address</th>
            <th>Company</th>
            <th>Role</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {pendingUsers.map((user) => (
            <tr key={user._id}>
              <td>{user.email}</td>
              <td>{user.phone}</td>
              <td>{user.address}</td>
              <td>{user.companyName || "â€”"}</td>
              <td>
                <Form.Select
                  onChange={(e) =>
                    setPendingUsers((prev) =>
                      prev.map((u) =>
                        u._id === user._id ? { ...u, selectedRole: e.target.value } : u
                      )
                    )
                  }
                >
                  <option value="">Select Role</option>
                  <option value="staff">Staff</option>
                  <option value="external_company">External Company</option>
                </Form.Select>
              </td>
              <td>
                <Button
                  variant="success"
                  className="me-2"
                  onClick={() =>
                    handleApprove(user._id, user.selectedRole)
                  }
                >
                  Approve
                </Button>
                <Button
                  variant="danger"
                  onClick={() => handleReject(user._id)}
                >
                  Reject
                </Button>
              </td>
            </tr>
          ))}
        </tbody>
      </Table>
    </div>
  );
};

export default PendingApproval;
