// src/components/AdminNavbar.js
import { useContext } from "react";
import { AuthContext } from "../context/AuthContext";

import React from "react";
import { Navbar, Nav, Container, Button } from "react-bootstrap";
import { useNavigate } from "react-router-dom";
import { Link } from "react-router-dom";

const AdminNavbar = () => {
  const navigate = useNavigate();
  const { user } = useContext(AuthContext);

  const handleLogout = () => {
    localStorage.removeItem("token");
    localStorage.removeItem("user");
    // navigate("/");
    window.location.href = "/"; // Force logout to redirect to Home
  };

  return (
    <Navbar bg="dark" variant="dark" expand="lg">
      <Container>
        <Navbar.Brand href="/admin">Kings Admin</Navbar.Brand>
        <Navbar.Toggle aria-controls="admin-navbar-nav" />
        <Navbar.Collapse id="admin-navbar-nav">
          <Nav className="me-auto">
            <Nav.Link href="/admin/pending-approval" className="nav-link">
              Pending Approval
            </Nav.Link>

            <Nav.Link as={Link} to="/admin/assets">
              Show Asset
            </Nav.Link>

            <Nav.Link href="/maintenance/ongoing">Ongoing Maintenance</Nav.Link>
            <Nav.Link as={Link} to="/admin/upcoming-maintenance">
              Upcoming Maintenance
            </Nav.Link>
            <Nav.Link as={Link} to="/admin/all-maintenance">
              All Maintenance Logs
            </Nav.Link>

            <Nav.Link href="/assign-task">Assign Task</Nav.Link>
            <Nav.Link href="/assessments">All Assessments</Nav.Link>
          </Nav>

          <Nav className="ms-auto">
            <Button variant="outline-light" onClick={handleLogout}>
              Logout
            </Button>
            <Nav.Link as={Link} to="/admin/profile">
            <div className="dropdown" style={{ marginLeft: "auto" }}>
  <button
    className="btn btn-outline-primary dropdown-toggle"
    type="button"
    id="userDropdown"
    data-bs-toggle="dropdown"
    aria-expanded="false"
    style={{ textTransform: "capitalize" }}
  >
    {user?.firstName}
  </button>
  <ul className="dropdown-menu dropdown-menu-end" aria-labelledby="userDropdown">
    <li>
      <a className="dropdown-item" href="/admin/profile">👤 Profile</a>
    </li>
    <li>
      <button className="dropdown-item text-danger" onClick={logout}>🚪 Logout</button>
    </li>
  </ul>
</div>

            </Nav.Link>
          </Nav>
        </Navbar.Collapse>
      </Container>
    </Navbar>
  );
};

export default AdminNavbar;
