import React, { useEffect, useState } from "react";
import axios from "axios";
import EmailModal from "../components/EmailModal";
import { Table, Button } from "react-bootstrap";
import AdminNavbar from "../components/AdminNavbar";

const AllMembers = () => {
  const [members, setMembers] = useState([]);
  const [showModal, setShowModal] = useState(false);
  const [selectedEmail, setSelectedEmail] = useState("");

  useEffect(() => {
    fetchMembers();
  }, []);

  const fetchMembers = async () => {
    try {
      const token = localStorage.getItem("token");
      const res = await axios.get("/api/all-members", {
        headers: { Authorization: `Bearer ${token}` },
      });
      setMembers(res.data);
    } catch (error) {
      console.error("Error fetching members:", error);
    }
  };

  const handleSendEmail = (email) => {
    setSelectedEmail(email); // ‚úÖ pre-fill TO:
    setShowModal(true);
  };

  const handleEmailSubmit = async ({ to, subject, message }) => {
    try {
      const token = localStorage.getItem("token");
      await axios.post(
        "/api/email/send",
        { to, subject, message },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      alert("‚úÖ Email sent successfully!");
      setShowModal(false);
    } catch (error) {
      console.error("Error sending email:", error);
      alert("‚ùå Failed to send email.");
    }
  };

  const handleDelete = async (userId) => {
    if (!window.confirm("Are you sure you want to delete this user?")) return;
    try {
      const token = localStorage.getItem("token");
      await axios.delete(`/api/all-members/${userId}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      fetchMembers(); // Refresh list
      alert("‚úÖ User deleted successfully");
    } catch (error) {
      console.error("Delete failed", error);
      alert("‚ùå Failed to delete user");
    }
  };

  return (
    <>
      <AdminNavbar />

      <div className="container mt-5">
        <h2>üìù All Approved Members</h2>

        <Table striped bordered hover responsive className="mt-4 shadow-sm">
          <thead className="table-dark text-center">
            <tr>
              <th>#</th>
              <th>Full Name</th>
              <th>Email</th>
              <th>Role</th>
              <th>Status</th>
              <th>Send Email</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody className="text-center">
            {members.length > 0 ? (
              members.map((user, index) => (
                <tr key={user._id}>
                  <td>{index + 1}</td>
                  <td>{user.firstName} {user.lastName}</td>
                  <td>{user.email}</td>
                  <td>
                    <span className="badge bg-info text-dark">{user.role}</span>
                  </td>
                  <td>
                    <span className="badge bg-success">Approved</span>
                  </td>
                  <td>
                    <button
                      className="btn btn-outline-primary btn-sm"
                      onClick={() => handleSendEmail(user.email)}
                    >
                      üìß Send Email
                    </button>
                  </td>
                  <td>
                    <button
                      className="btn btn-outline-warning btn-sm me-2"
                      onClick={() => alert("Edit user placeholder")}
                    >
                      ‚úèÔ∏è Edit
                    </button>
                    <button
                      className="btn btn-outline-danger btn-sm"
                      onClick={() => handleDelete(user._id)}
                    >
                      üóëÔ∏è Delete
                    </button>
                  </td>
                </tr>
              ))
            ) : (
              <tr>
                <td colSpan="7">No approved users found.</td>
              </tr>
            )}
          </tbody>
        </Table>
      </div>

      <EmailModal
        show={showModal}
        handleClose={() => setShowModal(false)}
        to={selectedEmail}
        onSubmit={handleEmailSubmit}
      />
    </>
  );
};

export default AllMembers;
