import React, { useEffect, useState } from "react";
import axios from "axios";
import { Table, Button, Modal, Form, Alert } from "react-bootstrap";

const ShowAssets = () => {
  const [assets, setAssets] = useState([]);
  const [showModal, setShowModal] = useState(false);
  const [showAlert, setShowAlert] = useState({
    show: false,
    message: "",
    variant: "",
  });
  const [editMode, setEditMode] = useState(false);
  const [currentId, setCurrentId] = useState(null);
  const [searchQuery, setSearchQuery] = useState("");


  const [formAsset, setFormAsset] = useState({
    assetName: "",
    assetType: "building",
    address: "",
    size: "",
    status: "active",
    description: "",
  });

  const fetchAssets = async () => {
    try {
      const res = await axios.get("/api/assets");
      setAssets(res.data);
    } catch (err) {
      console.error("Error fetching assets:", err);
    }
  };

  useEffect(() => {
    fetchAssets();
  }, []);

  const handleChange = (e) => {
    setFormAsset({ ...formAsset, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      if (editMode) {
        const confirmUpdate = window.confirm(
          "Are you sure you want to update this asset?"
        );
        if (!confirmUpdate) return;

        await axios.put(`/api/assets/${currentId}`, formAsset);
        showTimedAlert("‚úèÔ∏è Asset updated successfully", "info");
      } else {
        await axios.post("/api/assets", formAsset);
        showTimedAlert("‚úÖ Asset added successfully", "success");
      }
      closeModalAndReset();
      fetchAssets();
    } catch (err) {
      console.error("Error saving asset:", err);
    }
  };

  const handleEdit = (asset) => {
    setEditMode(true);
    setCurrentId(asset._id);
    setFormAsset(asset);
    setShowModal(true);
  };

  const handleDelete = async (id) => {
    if (!window.confirm("Are you sure you want to delete this asset?")) return;
    try {
      await axios.delete(`/api/assets/${id}`);
      showTimedAlert("üóëÔ∏è Asset deleted", "danger");
      fetchAssets();
    } catch (err) {
      console.error("Error deleting asset:", err);
    }
  };

  const closeModalAndReset = () => {
    setShowModal(false);
    setEditMode(false);
    setCurrentId(null);
    setFormAsset({
      assetName: "",
      assetType: "building",
      address: "",
      size: "",
      status: "active",
      description: "",
    });
  };

  const showTimedAlert = (message, variant) => {
    setShowAlert({ show: true, message, variant });
    setTimeout(
      () => setShowAlert({ show: false, message: "", variant: "" }),
      2500
    );
  };

  return (
    <div className="container mt-5">
      <div className="d-flex justify-content-between align-items-center mb-4">
        <h2>üè¢ All Assets</h2>
        <input
  type="text"
  placeholder="Search by name, type, or status"
  className="form-control mb-3"
  value={searchQuery}
  onChange={(e) => setSearchQuery(e.target.value)}
  style={{ maxWidth: "300px", float: "right" }}
/>

        <Button variant="primary" onClick={() => setShowModal(true)}>
          ‚ûï Add New Asset
        </Button>
      </div>
      

      {showAlert.show && (
        <Alert variant={showAlert.variant}>{showAlert.message}</Alert>
      )}

      <Table striped bordered hover responsive>
        <thead>
          <tr>
            <th>Asset Name</th>
            <th>Type</th>
            <th>Address</th>
            <th>Size</th>
            <th>Status</th>
            <th>Description</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {assets
          .filter((asset) => {
    const query = searchQuery.toLowerCase();
    return (
      asset.assetName.toLowerCase().includes(query) ||
      asset.assetType.toLowerCase().includes(query) ||
      asset.status.toLowerCase().includes(query)
    );
  })
          .map((asset) => (
            <tr key={asset._id}>
              <td>{asset.assetName}</td>
              <td>{asset.assetType.charAt(0).toUpperCase() + asset.assetType.slice(1)}</td>
              <td>{asset.address}</td>
              <td>{asset.size}</td>
              <td>{asset.status}</td>
              <td>{asset.description}</td>
              <td>
                <Button
                  variant="warning"
                  size="sm"
                  className="me-2"
                  onClick={() => handleEdit(asset)}
                >
                  Edit
                </Button>
                <Button
                  variant="danger"
                  size="sm"
                  onClick={() => handleDelete(asset._id)}
                >
                  Delete
                </Button>
              </td>
            </tr>
          ))}
        </tbody>
      </Table>

      {/* Modal */}
      <Modal show={showModal} onHide={closeModalAndReset}>
        <Modal.Header closeButton>
          <Modal.Title>{editMode ? "Edit Asset" : "Add New Asset"}</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form onSubmit={handleSubmit}>
            <Form.Group>
              <Form.Label>Asset Name</Form.Label>
              <Form.Control
                type="text"
                name="assetName"
                value={formAsset.assetName}
                onChange={handleChange}
                required
              />
            </Form.Group>

            <Form.Group className="mt-2">
              <Form.Label>Asset Type</Form.Label>
              <Form.Select
                name="assetType"
                value={formAsset.assetType}
                onChange={handleChange}
              >
                <option value="apartment">Apartment</option>
                <option value="building">Building</option>
                <option value="computer">Computer</option>
                <option value="furniture">Furniture</option>
                <option value="room">Room</option>
              </Form.Select>
            </Form.Group>

            <Form.Group className="mt-2">
              <Form.Label>Address</Form.Label>
              <Form.Control
                type="text"
                name="address"
                value={formAsset.address}
                onChange={handleChange}
                required
              />
            </Form.Group>

            <Form.Group className="mt-2">
              <Form.Label>Size</Form.Label>
              <Form.Control
                type="text"
                name="size"
                value={formAsset.size}
                onChange={handleChange}
              />
            </Form.Group>

            <Form.Group className="mt-2">
              <Form.Label>Status</Form.Label>
              <Form.Select
                name="status"
                value={formAsset.status}
                onChange={handleChange}
              >
                <option value="active">Active</option>
                <option value="inactive">Inactive</option>
                <option value="under_maintenance">Under Maintenance</option>
              </Form.Select>
            </Form.Group>

            <Form.Group className="mt-2">
              <Form.Label>Description</Form.Label>
              <Form.Control
                as="textarea"
                name="description"
                rows={3}
                value={formAsset.description}
                onChange={handleChange}
              />
            </Form.Group>

            <Button variant="success" type="submit" className="mt-3">
              {editMode ? "Update Asset" : "Save Asset"}
            </Button>
          </Form>
        </Modal.Body>
      </Modal>
    </div>
  );
};

export default ShowAssets;
