// src/components/AdminNavbar.js
import { useContext } from "react";
import { AuthContext } from "../context/AuthContext";

import React from "react";
import { Navbar, Nav, Container, Button } from "react-bootstrap";
import { useNavigate } from "react-router-dom";
import { Link } from "react-router-dom";
import Dropdown from 'react-bootstrap/Dropdown';


const AdminNavbar = () => {
  const navigate = useNavigate();
  const { user } = useContext(AuthContext);

  const handleLogout = () => {
    localStorage.removeItem("token");
    localStorage.removeItem("user");
    // navigate("/");
    window.location.href = "/"; // Force logout to redirect to Home
  };

  return (
    <Navbar bg="dark" variant="dark" expand="lg">
      <Container>
        <Navbar.Brand href="/admin">Kings Admin</Navbar.Brand>
        <Navbar.Toggle aria-controls="admin-navbar-nav" />
        <Navbar.Collapse id="admin-navbar-nav">
          <Nav className="me-auto">
            <Nav.Link href="/admin/pending-approval" className="nav-link">
              Pending Approval
            </Nav.Link>

            <Nav.Link as={Link} to="/admin/assets">
              Show Asset
            </Nav.Link>

            <Nav.Link href="/maintenance/ongoing">Ongoing Maintenance</Nav.Link>
            <Nav.Link as={Link} to="/admin/upcoming-maintenance">
              Upcoming Maintenance
            </Nav.Link>
            <Nav.Link as={Link} to="/admin/all-maintenance">
              All Maintenance Logs
            </Nav.Link>

            <Nav.Link href="/assign-task">Assign Task</Nav.Link>
            <Nav.Link href="/assessments">All Assessments</Nav.Link>
          </Nav>

          <Nav className="ms-auto">
            <Button variant="outline-light" onClick={handleLogout}>
              Logout
            </Button>
          </Nav>
          import Dropdown from 'react-bootstrap/Dropdown';

<Dropdown align="end">
  <Dropdown.Toggle variant="outline-primary" id="dropdown-basic" style={{ textTransform: "capitalize" }}>
    {user?.firstName}
  </Dropdown.Toggle>

  <Dropdown.Menu>
    <Dropdown.Item href="/admin/profile">ðŸ‘¤ Profile</Dropdown.Item>
    <Dropdown.Item onClick={logout} className="text-danger">ðŸšª Logout</Dropdown.Item>
  </Dropdown.Menu>
</Dropdown>

        </Navbar.Collapse>
      </Container>
    </Navbar>
  );
};

export default AdminNavbar;
