// src/pages/AllMembers.js
import React, { useEffect, useState } from "react";
import axios from "axios";
import EmailModal from "../components/EmailModal";
import { Table, Button } from "react-bootstrap";
import AdminNavbar from "../components/AdminNavbar";
import ViewUserModal from "../components/ViewUserModal";
import EditUserModal from "../components/EditUserModal";



const AllMembers = () => {
  const [members, setMembers] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");
  const [showModal, setShowModal] = useState(false);
  const [selectedEmail, setSelectedEmail] = useState("");
  const [showViewModal, setShowViewModal] = useState(false);
  const [selectedUser, setSelectedUser] = useState(null);
  const [showEditModal, setShowEditModal] = useState(false);
  const [editUser, setEditUser] = useState(null);



  useEffect(() => {
    fetchMembers();
  }, []);

  const fetchMembers = async () => {
    try {
      const token = localStorage.getItem("token");
      const res = await axios.get("/api/all-members/approved", {
        headers: { Authorization: `Bearer ${token}` },
      });
      setMembers(res.data);
    } catch (error) {
      console.error("Error fetching members:", error);
    }
  };

  const handleSendEmail = (email) => {
    setSelectedEmail(email);
    setShowModal(true);
  };

  const handleEmailSubmit = async ({ to, subject, message }) => {
    try {
      const token = localStorage.getItem("token");
      await axios.post(
        "/api/email/send",
        { to, subject, message },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      alert("✅ Email sent successfully!");
      setShowModal(false);
    } catch (error) {
      console.error("Error sending email:", error);
      alert("❌ Failed to send email.");
    }
  };

  const handleDelete = async (userId) => {
    if (!window.confirm("Are you sure you want to delete this user?")) return;
    try {
      const token = localStorage.getItem("token");
      await axios.delete(`/api/all-members/${userId}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      fetchMembers(); // Refresh list
      alert("✅ User deleted successfully");
    } catch (error) {
      console.error("Delete failed", error);
      alert("❌ Failed to delete user");
    }
  };

  const handleViewUser = (user) => {
    setSelectedUser(user);
    setShowViewModal(true);
  };

  const handleEditUser = (user) => {
    setEditUser(user);
    setShowEditModal(true);
  };

  const handleSaveChanges = async (updatedUser) => {
    try {
      const token = localStorage.getItem("token");
      await axios.put(`/api/all-members/${updatedUser._id}`, updatedUser, {
        headers: { Authorization: `Bearer ${token}` },
      });
      alert("✅ User updated successfully!");
      setShowEditModal(false);
      fetchMembers(); // Refresh list
    } catch (error) {
      console.error("Update failed:", error);
      alert("❌ Failed to update user");
    }
  };

  const filteredMembers = members.filter((user) => {
    const fullName = `${user.firstName} ${user.lastName}`.toLowerCase();
    const role = user.role.toLowerCase();
    return (
      fullName.includes(searchTerm.toLowerCase()) ||
      role.includes(searchTerm.toLowerCase())
    );
  });
  
  

  return (
    <>
      <AdminNavbar />

      <div className="container mt-5">
        <h2>👥 All Members</h2>

        <div className="mb-3 mt-4 d-flex justify-content-end">
          <input
            type="text"
            className="form-control w-25"
            placeholder="🔍 Search by name or role"
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
          />
        </div>

        <Table striped bordered hover responsive className="mt-4 shadow-sm">
          <thead className="table-dark text-center">
            <tr>
              <th>#</th>
              <th>👤 Full Name</th>
              <th>📧 Email</th>
              <th>🔐 Role</th>
              <th>📌 Status</th>
              <th>✉️ Email</th>
              <th>✏️🗑️👁️ Actions</th>
            </tr>
          </thead>



          <tbody className="text-center">
            {filteredMembers.length > 0 ? (
              filteredMembers.map((user, index) => (
                <tr key={user._id}>
                  <td>{index + 1}</td>
                  <td className="text-capitalize">
                    {user.firstName} {user.lastName}
                  </td>
                  <td>
                    <a href={`mailto:${user.email}`}>{user.email}</a>
                  </td>
                  <td className="text-capitalize">
                    <span className="badge bg-info text-dark">{user.role}</span>
                  </td>
                  <td>
                    <span className="badge bg-success">Approved</span>
                  </td>
                  <td>
                    <Button
                      variant="outline-primary"
                      size="sm"
                      onClick={() => handleSendEmail(user.email)}
                    >
                      📤 Send
                    </Button>
                  </td>
                  <td>
                    <Button
                      variant="outline-secondary"
                      size="sm"
                      className="me-2"
                      onClick={() => handleViewUser(user)} // ✅ use full user object
                    >
                      👁️ View
                    </Button>

                    <Button
                      variant="outline-warning"
                      size="sm"
                      className="me-2"
                      onClick={() => handleEditUser(user)}
                    >
                      ✏️ Edit
                    </Button>
                    <Button
                      variant="outline-danger"
                      size="sm"
                      onClick={() => handleDelete(user._id)}
                    >
                      🗑️ Delete
                    </Button>
                  </td>
                  <td colSpan="7" className="text-center">No matching members found.</td>
                </tr>
              ))
            ) : (
              <tr>
                <td colSpan="7">No approved users found.</td>
              </tr>
             
            )}
          </tbody>
        </Table>
      </div>

      <EmailModal
        show={showModal}
        handleClose={() => setShowModal(false)}
        userEmail={selectedEmail}
        onSubmit={handleEmailSubmit}
      />

      <ViewUserModal
        show={showViewModal}
        handleClose={() => setShowViewModal(false)}
        user={selectedUser}
      />

      <EditUserModal
        show={showEditModal}
        handleClose={() => setShowEditModal(false)}
        user={editUser}
        onSave={handleSaveChanges}
      />
    </>
  );
};

export default AllMembers;
