import React, { useEffect, useState } from "react";
import axios from "axios";
import { Table, Button, Form } from "react-bootstrap";
import AdminNavbar from "../components/AdminNavbar";

const UpcomingMaintenance = () => {
  const [assets, setAssets] = useState([]);
  const [updates, setUpdates] = useState({});

  useEffect(() => {
    fetchAssets();
  }, []);

  const fetchAssets = async () => {
    try {
      const res = await axios.get("/api/maintenance/upcoming-maintenance");
      setAssets(res.data);
    } catch (error) {
      console.error("Error fetching upcoming maintenance:", error);
    }
  };

  const handleInputChange = (assetId, field, value) => {
    setUpdates((prev) => ({
      ...prev,
      [assetId]: {
        ...prev[assetId],
        [field]: value,
      },
    }));
  };

  const handleSave = async (assetId) => {
    try {
      const payload = updates[assetId] || {};
      await axios.patch(`/api/maintenance/${assetId}/update`, payload);
      alert("Maintenance info saved âœ…");
      fetchAssets();
    } catch (error) {
      console.error("Error saving maintenance info:", error);
      alert("Failed to save. Please try again.");
    }
  };

  return (
    <>
    <AdminNavbar />
    <div className="container mt-4">
      <h2 className="mb-4">ðŸ”§ Upcoming Maintenance</h2>
      <Table striped bordered hover responsive>
        <thead>
          <tr>
            <th>Asset Name</th>
            <th>Location</th>
            <th>Status</th>
            <th>Assigned To</th>
            <th>Maintenance Date</th>
            <th>Maintenance Type</th>
            <th>Notes</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
          {assets.map((asset) => (
            <tr key={asset._id}>
              <td>{asset.assetName}</td>
              <td>{asset.location}</td>
              <td>{asset.status}</td>
              <td>
                {asset.assignedTo
                  ? `${asset.assignedTo.firstName} ${asset.assignedTo.lastName}`
                  : "â€”"}
              </td>
              <td>{asset.scheduledMaintenance?.slice(0, 10)}</td>
              <td>
                <Form.Control
                  type="text"
                  placeholder="Enter type"
                  value={updates[asset._id]?.maintenanceType || ""}
                  onChange={(e) =>
                    handleInputChange(asset._id, "maintenanceType", e.target.value)
                  }
                />
              </td>
              <td>
                <Form.Control
                  as="textarea"
                  placeholder="Enter notes"
                  rows={1}
                  value={updates[asset._id]?.maintenanceNotes || ""}
                  onChange={(e) =>
                    handleInputChange(asset._id, "maintenanceNotes", e.target.value)
                  }
                />
              </td>
              <td>
                <Button
                  variant="success"
                  size="sm"
                  onClick={() => handleSave(asset._id)}
                >
                  Save
                </Button>
              </td>
            </tr>
          ))}
        </tbody>
      </Table>
    </div>
    </>
  );
};

export default UpcomingMaintenance;
