import React, { useEffect, useState } from "react";
import axios from "axios";
import EmailModal from "../components/EmailModal";
import { Table, Button } from "react-bootstrap";
import AdminNavar from "../components/AdminNavbar";

const AllMembers = () => {
  const [members, setMembers] = useState([]);
  const [showModal, setShowModal] = useState(false);
  const [selectedEmail, setSelectedEmail] = useState("");

  useEffect(() => {
    const fetchMembers = async () => {
      try {
        const token = localStorage.getItem("token");
        const res = await axios.get("/api/all-members", {
          headers: { Authorization: `Bearer ${token}` },
        });
        setMembers(res.data);
      } catch (error) {
        console.error("Error fetching members:", error);
      }
    };

    fetchMembers();
  }, []);

  const handleSendEmail = (email) => {
    setSelectedEmail(email);
    setShowModal(true);
  };

  const handleEmailSubmit = async ({ to, subject, message }) => {
    try {
      const token = localStorage.getItem("token");
      await axios.post(
        "/api/all-members/send-email",
        { to, subject, message },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      alert("Email sent successfully!");
    } catch (error) {
      console.error("Error sending email:", error);
      alert("Failed to send email.");
    }
  };

  return (
    <>
    < AdminNavbar/>
   
    <div className="container mt-5">
    <h2>
      <span role="img" aria-label="doc">📝</span> All Approved Members
    </h2>
  
    <table className="table table-bordered table-hover mt-4 shadow-sm">
      <thead className="table-dark text-center">
        <tr>
          <th>#</th>
          <th>Full Name</th>
          <th>Email</th>
          <th>Role</th>
          <th>Status</th>
          <th>Send Email</th>
          <th>Actions</th>
        </tr>
      </thead>
      <tbody className="text-center">
        {members.length > 0 ? (
          members.map((user, index) => (
            <tr key={user._id}>
              <td>{index + 1}</td>
              <td>{user.firstName} {user.lastName}</td>
              <td>{user.email}</td>
              <td>
                <span className="badge bg-info text-dark">{user.role}</span>
              </td>
              <td>
                <span className="badge bg-success">Approved</span>
              </td>
              <td>
                <button
                  className="btn btn-outline-primary btn-sm"
                  onClick={() => handleSendEmail(user.email)}
                >
                  📧 Send Email
                </button>
              </td>
              <td>
                <button className="btn btn-outline-warning btn-sm me-2">
                  ✏️ Edit
                </button>
                <button className="btn btn-outline-danger btn-sm">
                  🗑️ Delete
                </button>
              </td>
            </tr>
          ))
        ) : (
          <tr>
            <td colSpan="7">No approved users found.</td>
          </tr>
        )}
      </tbody>
    </table>
  </div>
  </>
  );
};

export default AllMembers;
