import React, { useEffect, useState } from "react";
import axios from "axios";
import { Container, Table } from "react-bootstrap";
import { FaClipboardList } from "react-icons/fa";

const AllMaintenanceLogs = () => {
  const [logs, setLogs] = useState([]);

  useEffect(() => {
    const fetchLogs = async () => {
      try {
        const res = await axios.get("/api/maintenance/logs");
        setLogs(res.data);
      } catch (err) {
        console.error("Failed to fetch maintenance logs:", err);
      }
    };

    fetchLogs();
  }, []);

  return (
    <Container className="mt-5">
      <h2>
        <FaClipboardList className="me-2" />
        All Maintenance Logs
      </h2>

      <Table striped bordered hover responsive className="mt-4">
        <thead>
          <tr>
            <th>Asset Name</th>
            <th>Location</th>
            <th>Type</th>
            <th>Notes</th>
            <th>Scheduled Date</th>
            <th>Assigned To</th>
            <th>Created At</th>
          </tr>
        </thead>
        <tbody>
          {logs.map((task) => (
            <tr key={task._id}>
              <td>{task.asset?.assetName || "—"}</td>
              <td>{task.asset?.location || "—"}</td>
              <td>{task.type || "—"}</td>
              <td>{task.notes || "—"}</td>
              <td>{task.scheduledDate?.slice(0, 10) || "—"}</td>
              <td>
                {task.assignedTo
                  ? `${task.assignedTo.firstName} ${task.assignedTo.lastName}`
                  : "—"}
              </td>
              <td>{task.createdAt?.slice(0, 10) || "—"}</td>
            </tr>
          ))}
        </tbody>
      </Table>
    </Container>
  );
};

export default AllMaintenanceLogs;
