import React, { useEffect, useState } from "react";
import axios from "axios";
import { Button, Table, Alert, Form } from "react-bootstrap";

const PendingApproval = () => {
  const [pendingUsers, setPendingUsers] = useState([]);
  const [error, setError] = useState("");
  const [success, setSuccess] = useState("");
  const [selectedRoles, setSelectedRoles] = useState({});

  useEffect(() => {
    fetchPendingUsers();
  }, []);

  const fetchPendingUsers = async () => {
    try {
      const res = await axios.get("/api/users/pending", {
        headers: {
          Authorization: `Bearer ${localStorage.getItem("token")}`,
        },
      });
      setPendingUsers(res.data);
    } catch (err) {
      setError("Failed to fetch pending users");
    }
  };

  const handleApprove = async (userId, email, firstName) => {
    const selectedRole = selectedRoles[userId];
    if (!selectedRole) {
      alert("Please select a role before approving.");
      return;
    }

    try {
      await axios.patch(
        `/api/users/${userId}/approve`,
        {
          email,
          role: selectedRole,
          firstName,
        },
        {
          headers: {
            Authorization: `Bearer ${localStorage.getItem("token")}`,
          },
        }
      );
      setSuccess("User approved and email sent.");
      fetchPendingUsers();
    } catch (err) {
      console.error("Approval error:", err);
      setError("Approval failed. Please try again.");
    }
  };

  const handleReject = async (userId, email, firstName) => {
    try {
      await axios.patch(
        `/api/users/${userId}/reject`,
        {
          email: user.email,
        firstName: user.firstName,
        },
        {
          headers: {
            Authorization: `Bearer ${localStorage.getItem("token")}`,
          },
        }
      );
      setSuccess("User rejected and email sent.");
      fetchPendingUsers();
    } catch (err) {
      console.error("❌ Rejection error:", err);
      setError("Rejection failed.");
    }
  };

  return (
    <div className="container mt-4">
      <h3>🚦 Pending Approvals</h3>

      {error && <Alert variant="danger">{error}</Alert>}
      {success && <Alert variant="success">{success}</Alert>}

      <Table striped bordered hover>
        <thead>
          <tr>
            <th>Email</th>
            <th>Phone</th>
            <th>Address</th>
            <th>Company</th>
            <th>Role</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {pendingUsers.map((user) => (
            <tr key={user._id}>
              <td>{user.email}</td>
              <td>{user.phone}</td>
              <td>{user.address}</td>
              <td>{user.companyName || "—"}</td>
              <td>
                <Form.Select
                  value={selectedRoles[user._id] || ""}
                  onChange={(e) =>
                    setSelectedRoles({
                      ...selectedRoles,
                      [user._id]: e.target.value,
                    })
                  }
                >
                  <option value="">Select role</option>
                  <option value="staff">Staff</option>
                  <option value="external_company">External Company</option>
                </Form.Select>
              </td>
              <td>
                <Button
                  variant="success"
                  size="sm"
                  className="me-2"
                  onClick={() =>
                    handleApprove(user._id, user.email, user.firstName)
                  }
                >
                  Approve
                </Button>
                <Button
                  variant="danger"
                  size="sm"
                  onClick={() =>
                    handleReject(user._id, user.email, user.firstName)
                  }
                >
                  Reject
                </Button>
              </td>
            </tr>
          ))}
        </tbody>
      </Table>
    </div>
  );
};

export default PendingApproval;
