import React, { useState } from "react";
import { Button, Container, Row, Col, Modal } from "react-bootstrap";
import { useNavigate } from "react-router-dom";
import Signup from "./Signup";
import "./Home.css"; // üëà Custom CSS file

const Home = () => {
  const navigate = useNavigate();
  const [showSignup, setShowSignup] = useState(false);

  return (
    <div className="home-background">
      <Container className="d-flex flex-column justify-content-center align-items-center min-vh-100">
        <div className="glass-box text-center p-4">
          <h1 className="mb-3">Welcome to Kings Property Portal üè¢</h1>
          <p className="mb-4 lead">
            Manage your properties, assets, maintenance, and teams ‚Äî all in one platform.
          </p>
          <div className="d-flex justify-content-center gap-3">
            <Button variant="primary" onClick={() => navigate("/login")}>
              Login
            </Button>
            <Button variant="success" onClick={() => setShowSignup(true)}>
              Sign Up
            </Button>
          </div>
        </div>
      </Container>

      {/* Sign Up Modal */}
      <Modal show={showSignup} onHide={() => setShowSignup(false)}>
        <Modal.Header closeButton>
          <Modal.Title>Create Your Account</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Signup onSuccess={() => setShowSignup(false)} />
        </Modal.Body>
      </Modal>
    </div>
  );
};

export default Home;
