import React, { useState } from 'react';
import { StytchUIClient } from '@stytch/vanilla-js';

const stytch = new StytchUIClient('public-token-test-d6cbd1df-2327-49b2-8510-1be01be05658'); // use actual public token

const Login = () => {
  const [email, setEmail] = useState('');

  const handleMagicLinkLogin = async () => {
    if (!email) return alert('Please enter your email');

    try {
      await stytch.magicLinks.email.loginOrCreate({
        email: email,
        login_magic_link_url: 'http://localhost:3000',
        signup_magic_link_url: 'http://localhost:3000',
      });
      alert('Magic link sent!');
    } catch (error) {
      console.error('login error:', error);
      alert(`Error: ${error.message}`);
    }
  };

  return (
    <div style={{ textAlign: 'center', marginTop: '100px' }}>
      <h2>Login to Kings Portal</h2>
      <input
        type="email"
        value={email}
        onChange={(e) => setEmail(e.target.value)}
        placeholder="Enter email"
        style={{ padding: '8px', marginRight: '10px' }}
      />
      <button onClick={handleMagicLinkLogin}>Send Magic Link</button>
    </div>
  );
};

export default Login;
