import React, { useEffect, useState } from "react";
import axios from "axios";
import { saveAs } from "file-saver";
import { FaTools } from "react-icons/fa";

const UpcomingMaintenance = () => {
  const [assets, setAssets] = useState([]);
  const [filteredAssets, setFilteredAssets] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");
  const [maintenanceData, setMaintenanceData] = useState({});
  const [sortOrder, setSortOrder] = useState("desc");

  useEffect(() => {
    const fetchAssets = async () => {
      try {
        const res = await axios.get("/api/maintenance/upcoming-maintenance");
        setAssets(res.data);
      } catch (err) {
        console.error("Error fetching assets:", err);
      }
    };
    fetchAssets();
  }, []);

  useEffect(() => {
    let result = [...assets];

    if (searchTerm) {
      result = result.filter((asset) => {
        const assignedTo = asset.assignedTo
          ? `${asset.assignedTo.firstName} ${asset.assignedTo.lastName}`.toLowerCase()
          : "not assigned";
        return (
          asset.assetName.toLowerCase().includes(searchTerm.toLowerCase()) ||
          asset.location.toLowerCase().includes(searchTerm.toLowerCase()) ||
          asset.status.toLowerCase().includes(searchTerm.toLowerCase()) ||
          assignedTo.includes(searchTerm.toLowerCase())
        );
      });
    }

    result.sort((a, b) => {
      const dateA = new Date(a.scheduledMaintenance);
      const dateB = new Date(b.scheduledMaintenance);
      return sortOrder === "asc" ? dateA - dateB : dateB - dateA;
    });

    setFilteredAssets(result);
  }, [searchTerm, assets, sortOrder]);

  const handleChange = (id, field, value) => {
    setMaintenanceData({
      ...maintenanceData,
      [id]: {
        ...maintenanceData[id],
        [field]: value,
      },
    });
  };

  const handleSave = async (asset) => {
    const data = maintenanceData[asset._id];
    if (!data || !data.maintenanceType || !data.maintenanceNotes) return;

    try {
      await axios.post("/api/maintenance/save-task", {
        assetId: asset._id,
        maintenanceType: data.maintenanceType,
        maintenanceNotes: data.maintenanceNotes,
        scheduledDate: asset.scheduledMaintenance,
        assignedTo: asset.assignedTo?._id || null,
      });
      alert("Maintenance task saved and email sent.");
    } catch (err) {
      console.error("Error saving task:", err);
    }
  };

  const handleExportCSV = () => {
    const csvRows = [
      [
        "Asset Name",
        "Location",
        "Status",
        "Assigned To",
        "Maintenance Date",
      ],
      ...filteredAssets.map((asset) => [
        asset.assetName,
        asset.location,
        asset.status,
        asset.assignedTo
          ? `${asset.assignedTo.firstName} ${asset.assignedTo.lastName}`
          : "Not Assigned",
        new Date(asset.scheduledMaintenance).toLocaleDateString(),
      ]),
    ];

    const csvContent = csvRows.map((row) => row.join(",")).join("\n");
    const blob = new Blob([csvContent], { type: "text/csv;charset=utf-8;" });
    saveAs(blob, "upcoming_maintenance.csv");
  };

  const isWithin7Days = (date) => {
    const today = new Date();
    const maintenanceDate = new Date(date);
    const diffTime = maintenanceDate - today;
    const diffDays = diffTime / (1000 * 60 * 60 * 24);
    return diffDays >= 0 && diffDays <= 7;
  };

  return (
    <div className="container mt-4">
      <h2 className="mb-4">
        <FaTools className="me-2" /> Upcoming Maintenance
      </h2>

      <input
        type="text"
        className="form-control mb-3"
        placeholder="Search by asset, location, status or person"
        value={searchTerm}
        onChange={(e) => setSearchTerm(e.target.value)}
      />

      <div className="d-flex justify-content-between align-items-center mb-3">
        <button className="btn btn-success" onClick={handleExportCSV}>
          Export CSV
        </button>
        <div className="d-flex align-items-center">
          <label className="me-2 fw-bold">Sort by:</label>
          <select
            className="form-select"
            style={{ width: "180px" }}
            value={sortOrder}
            onChange={(e) => setSortOrder(e.target.value)}
          >
            <option value="desc">Latest First</option>
            <option value="asc">Earliest First</option>
          </select>
        </div>
      </div>

      <table className="table table-bordered">
        <thead>
          <tr>
            <th>Asset Name</th>
            <th>Location</th>
            <th>Status</th>
            <th>Assigned To</th>
            <th>Maintenance Date</th>
            <th>Type</th>
            <th>Notes</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
          {filteredAssets.map((asset) => (
            <tr
              key={asset._id}
              className={isWithin7Days(asset.scheduledMaintenance) ? "table-warning" : ""}
            >
              <td>{asset.assetName}</td>
              <td>{asset.location}</td>
              <td>{asset.status}</td>
              <td>
                {asset.assignedTo
                  ? `${asset.assignedTo.firstName} ${asset.assignedTo.lastName}`
                  : "Not Assigned"}
              </td>
              <td>{new Date(asset.scheduledMaintenance).toLocaleDateString()}</td>
              <td>
                <input
                  type="text"
                  className="form-control"
                  placeholder="e.g. Inspection"
                  value={maintenanceData[asset._id]?.maintenanceType || ""}
                  onChange={(e) =>
                    handleChange(asset._id, "maintenanceType", e.target.value)
                  }
                />
              </td>
              <td>
                <textarea
                  className="form-control"
                  placeholder="Write notes..."
                  value={maintenanceData[asset._id]?.maintenanceNotes || ""}
                  onChange={(e) =>
                    handleChange(asset._id, "maintenanceNotes", e.target.value)
                  }
                />
              </td>
              <td>
                <button
                  className="btn btn-primary"
                  onClick={() => handleSave(asset)}
                >
                  Save
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default UpcomingMaintenance;
