import React, { useEffect, useState } from "react";
import axios from "axios";
import { Modal, Button, Form } from "react-bootstrap";

const ShowAssets = () => {
  const [assets, setAssets] = useState([]);
  const [searchQuery, setSearchQuery] = useState("");
  const [showUserModal, setShowUserModal] = useState(false);
  const [selectedAssetId, setSelectedAssetId] = useState(null);
  const [availableUsers, setAvailableUsers] = useState([]);

  const [showEditModal, setShowEditModal] = useState(false);
  const [editFormData, setEditFormData] = useState({});
  const [showAddModal, setShowAddModal] = useState(false);
  const [newAsset, setNewAsset] = useState({
    assetName: "",
    assetType: "",
    status: "",
    purchaseDate: "",
    currentValue: "",
    scheduleMaintenance: "",
  });

  // Fetch assets
  const fetchAssets = async () => {
    try {
      const response = await axios.get("/api/assets");
      setAssets(response.data);
    } catch (error) {
      console.error("Error fetching assets:", error);
    }
  };

  useEffect(() => {
    fetchAssets();
  }, []);

  // Search Filter
  const filteredAssets = assets.filter((asset) => {
    const query = searchQuery.toLowerCase();
    return (
      asset.assetName.toLowerCase().includes(query) ||
      asset.assetType.toLowerCase().includes(query) ||
      asset.status.toLowerCase().includes(query)
    );
  });

  // Add asset
  const handleAddAsset = async () => {
    try {
      await axios.post("/api/assets", newAsset);
      fetchAssets();
      alert("✅ Asset added successfully!");
      setShowAddModal(false);
      setNewAsset({
        assetName: "",
        assetType: "",
        status: "",
        purchaseDate: "",
        currentValue: "",
        scheduleMaintenance: "",
      });
    } catch (error) {
      console.error("Error adding asset:", error);
    }
  };

  // Edit asset modal trigger
  const handleEditClick = (asset) => {
    setEditFormData(asset);
    setShowEditModal(true);
  };

  // Update asset
  const handleUpdateAsset = async () => {
    const confirmUpdate = window.confirm("Are you sure you want to update?");
    if (!confirmUpdate) return;

    try {
      await axios.put(`/api/assets/${editFormData._id}`, editFormData);
      fetchAssets();
      alert("✅ Asset updated successfully!");
      setShowEditModal(false);
    } catch (error) {
      console.error("Error updating asset:", error);
    }
  };

  // Delete asset
  const handleDelete = async (id) => {
    const confirmDelete = window.confirm("Are you sure to delete?");
    if (!confirmDelete) return;

    try {
      await axios.delete(`/api/assets/${id}`);
      fetchAssets();
      alert("✅ Asset deleted successfully!");
    } catch (error) {
      console.error("Error deleting asset:", error);
    }
  };

  // Fetch staff/external users for assignment
  const handleShowUserModal = async (assetId) => {
    try {
      const response = await axios.get("/api/users/assignable");
      setAvailableUsers(response.data);
      setSelectedAssetId(assetId);
      setShowUserModal(true);
    } catch (error) {
      console.error("Error fetching users:", error);
    }
  };

  const handleAssignUser = async (userId) => {
    try {
      await axios.patch(`/api/assets/${selectedAssetId}/assign`, { userId });
      alert("✅ Assigned successfully & user notified via email.");
      setShowUserModal(false);
      fetchAssets();
    } catch (error) {
      console.error("Error assigning user:", error);
    }
  };

  return (
    <div className="container mt-4">
      <h2 className="mb-4">📦 All Assets</h2>

      <div className="d-flex justify-content-between mb-3">
        <input
          type="text"
          placeholder="Search by Name, Type, or Status"
          className="form-control w-50"
          value={searchQuery}
          onChange={(e) => setSearchQuery(e.target.value)}
        />

        <Button variant="primary" onClick={() => setShowAddModal(true)}>
          ➕ Add Asset
        </Button>
      </div>

      <table className="table table-striped table-bordered">
        <thead>
          <tr>
            <th>Asset Name</th>
            <th>Type</th>
            <th>Status</th>
            <th>Purchase Date</th>
            <th>Value</th>
            <th>Maintenance Date</th>
            <th>Assigned To</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {filteredAssets.map((asset) => (
            <tr key={asset._id}>
              <td>{asset.assetName}</td>
              <td>
                {asset.assetType.charAt(0).toUpperCase() +
                  asset.assetType.slice(1)}
              </td>
              <td>{asset.status}</td>
              <td>{asset.purchaseDate}</td>
              <td>{asset.currentValue}</td>
              <td>{asset.scheduleMaintenance}</td>
              <td>
                {asset.assignedTo ? (
                  <span>{asset.assignedTo.name || "Assigned"}</span>
                ) : (
                  <button
                    className="btn btn-sm btn-info"
                    onClick={() => handleShowUserModal(asset._id)}
                  >
                    Show Staff
                  </button>
                )}
              </td>
              <td>
                <button
                  className="btn btn-sm btn-warning me-2"
                  onClick={() => handleEditClick(asset)}
                >
                  Edit
                </button>
                <button
                  className="btn btn-sm btn-danger"
                  onClick={() => handleDelete(asset._id)}
                >
                  Delete
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      {/* Add Modal */}
      <Modal show={showAddModal} onHide={() => setShowAddModal(false)}>
        <Modal.Header closeButton>
          <Modal.Title>Add Asset</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form>
            <Form.Group className="mb-2">
              <Form.Label>Asset Name</Form.Label>
              <Form.Control
                value={newAsset.assetName}
                onChange={(e) =>
                  setNewAsset({ ...newAsset, assetName: e.target.value })
                }
              />
            </Form.Group>
            <Form.Group className="mb-2">
              <Form.Label>Asset Type</Form.Label>
              <Form.Select
                value={newAsset.assetType}
                onChange={(e) =>
                  setNewAsset({ ...newAsset, assetType: e.target.value })
                }
              >
                <option value="">Select type</option>
                <option value="computer">Computer</option>
                <option value="furniture">Furniture</option>
              </Form.Select>
            </Form.Group>
            <Form.Group className="mb-2">
              <Form.Label>Status</Form.Label>
              <Form.Control
                value={newAsset.status}
                onChange={(e) =>
                  setNewAsset({ ...newAsset, status: e.target.value })
                }
              />
            </Form.Group>
            <Form.Group className="mb-2">
              <Form.Label>Purchase Date</Form.Label>
              <Form.Control
                type="date"
                value={newAsset.purchaseDate}
                onChange={(e) =>
                  setNewAsset({ ...newAsset, purchaseDate: e.target.value })
                }
              />
            </Form.Group>
            <Form.Group className="mb-2">
              <Form.Label>Current Value</Form.Label>
              <Form.Control
                value={newAsset.currentValue}
                onChange={(e) =>
                  setNewAsset({ ...newAsset, currentValue: e.target.value })
                }
              />
            </Form.Group>
            <Form.Group>
              <Form.Label>Schedule Maintenance</Form.Label>
              <Form.Control
                type="date"
                value={newAsset.scheduleMaintenance}
                onChange={(e) =>
                  setNewAsset({
                    ...newAsset,
                    scheduleMaintenance: e.target.value,
                  })
                }
              />
            </Form.Group>
          </Form>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={() => setShowAddModal(false)}>
            Cancel
          </Button>
          <Button variant="success" onClick={handleAddAsset}>
            Add
          </Button>
        </Modal.Footer>
      </Modal>

      {/* Edit Modal */}
      <Modal show={showEditModal} onHide={() => setShowEditModal(false)}>
        <Modal.Header closeButton>
          <Modal.Title>Edit Asset</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form>
            {Object.keys(editFormData).map((key) =>
              ["_id", "__v", "assignedTo"].includes(key) ? null : (
                <Form.Group key={key} className="mb-2">
                  <Form.Label>{key}</Form.Label>
                  <Form.Control
                    value={editFormData[key]}
                    onChange={(e) =>
                      setEditFormData({
                        ...editFormData,
                        [key]: e.target.value,
                      })
                    }
                  />
                </Form.Group>
              )
            )}
          </Form>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={() => setShowEditModal(false)}>
            Cancel
          </Button>
          <Button variant="warning" onClick={handleUpdateAsset}>
            Update
          </Button>
        </Modal.Footer>
      </Modal>

      {/* Assign User Modal */}
      <Modal show={showUserModal} onHide={() => setShowUserModal(false)}>
        <Modal.Header closeButton>
          <Modal.Title>Assign User to Asset</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          {availableUsers.length > 0 ? (
            <ul className="list-group">
              {availableUsers.map((user) => (
                <li
                  key={user._id}
                  className="list-group-item d-flex justify-content-between align-items-center"
                >
                  <span>
                    {user.firstName} {user.lastName} ({user.role})
                  </span>
                  <button
                    className="btn btn-sm btn-primary"
                    onClick={() => handleAssignUser(user._id)}
                  >
                    Assign
                  </button>
                </li>
              ))}
            </ul>
          ) : (
            <p>No approved staff/external users available.</p>
          )}
        </Modal.Body>
      </Modal>
    </div>
  );
};

export default ShowAssets;
