import React, { useEffect, useState } from "react";
import axios from "axios";
import { Table, Button, Alert, Form } from "react-bootstrap";

const PendingApproval = () => {
  const [pendingUsers, setPendingUsers] = useState([]);
  const [error, setError] = useState("");
  const [success, setSuccess] = useState("");

  useEffect(() => {
    fetchPendingUsers();
  }, []);

  const fetchPendingUsers = async () => {
    try {
      const res = await axios.get("/api/users");
      const unapproved = res.data.filter((user) => user.approved === false);
      setPendingUsers(unapproved);
    } catch (err) {
      console.error("Error fetching users:", err);
      setError("Failed to fetch pending users.");
    }
  };

  const handleApprove = async (user, role) => {
    const confirm = window.confirm(`Approve ${user.firstName}?`);
    if (!confirm) return;

    try {
      await axios.patch(`/api/users/${user._id}/approve`, {
        email: user.email,
        firstName: user.firstName,
        role,
      });
      setSuccess(`${user.firstName} has been approved and notified.`);
      fetchPendingUsers();
    } catch (err) {
      console.error("Error approving user:", err);
      setError("Approval failed.");
    }
  };

  const handleReject = async (user) => {
    const confirm = window.confirm(`Reject ${user.firstName}?`);
    if (!confirm) return;

    try {
      await axios.patch(`/api/users/${user._id}/reject`, {
        email: user.email,
        firstName: user.firstName,
      });
      setSuccess(`${user.firstName} has been rejected and notified.`);
      fetchPendingUsers();
    } catch (err) {
      console.error("Error rejecting user:", err);
      setError("Rejection failed.");
    }
  };

  return (
    <div className="container mt-5">
      <h2>ðŸš¦ Pending Approvals</h2>

      {error && <Alert variant="danger">{error}</Alert>}
      {success && <Alert variant="success">{success}</Alert>}

      <Table striped bordered hover responsive className="mt-4">
        <thead>
          <tr>
            <th>Email</th>
            <th>Phone</th>
            <th>Address</th>
            <th>Company</th>
            <th>Role</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {pendingUsers.map((user) => (
            <tr key={user._id}>
              <td>{user.email}</td>
              <td>{user.phone}</td>
              <td>{user.address}</td>
              <td>{user.companyName || "â€”"}</td>
              <td>
                <Form.Select
                  onChange={(e) =>
                    setPendingUsers((prev) =>
                      prev.map((u) =>
                        u._id === user._id
                          ? { ...u, selectedRole: e.target.value }
                          : u
                      )
                    )
                  }
                  defaultValue=""
                >
                  <option value="" disabled>
                    Select Role
                  </option>
                  <option value="staff">Staff</option>
                  <option value="external_company">External Company</option>
                </Form.Select>
              </td>
              <td>
                <Button
                  variant="success"
                  className="me-2"
                  onClick={() =>
                    handleApprove(user, user.selectedRole || "external_company")
                  }
                >
                  Approve
                </Button>
                <Button variant="danger" onClick={() => handleReject(user)}>
                  Reject
                </Button>
              </td>
            </tr>
          ))}
        </tbody>
      </Table>
    </div>
  );
};

export default PendingApproval;
