// src/components/AdminNavbar.js
import { useContext } from "react";
import { AuthContext } from "../context/AuthContext";

import React from "react";
import { Navbar, Nav, Container, Button } from "react-bootstrap";
import { useNavigate } from "react-router-dom";
import { Link } from "react-router-dom";
import Dropdown from "react-bootstrap/Dropdown";
import { FaUser, FaSignOutAlt, FaMoon } from "react-icons/fa";
import "./AdminNavbar.css"; // custom styles here



const AdminNavbar = ({ logout }) => {
  const navigate = useNavigate();
  const { user } = useContext(AuthContext);
  const firstInitial = user?.firstName?.[0]?.toUpperCase() || "?";

  const handleLogout = () => {
    localStorage.removeItem("token");
    localStorage.removeItem("user");
    // navigate("/");
    window.location.href = "/"; // Force logout to redirect to Home
  };

  return (
    <Navbar bg="dark" variant="dark" expand="lg">
      <Container>
        <Navbar.Brand href="/admin">Kings Admin</Navbar.Brand>
        <Navbar.Toggle aria-controls="admin-navbar-nav" />
        <Navbar.Collapse id="admin-navbar-nav">
          <Nav className="me-auto">
            <Nav.Link href="/admin/pending-approval" className="nav-link">
              Pending Approval
            </Nav.Link>

            <Nav.Link as={Link} to="/admin/assets">
              Show Asset
            </Nav.Link>

            <Nav.Link href="/maintenance/ongoing">Ongoing Maintenance</Nav.Link>
            <Nav.Link as={Link} to="/admin/upcoming-maintenance">
              Upcoming Maintenance
            </Nav.Link>
            <Nav.Link as={Link} to="/admin/all-maintenance">
              All Maintenance Logs
            </Nav.Link>

            <Nav.Link href="/assign-task">Assign Task</Nav.Link>
            <Nav.Link href="/assessments">All Assessments</Nav.Link>
          </Nav>

          <Dropdown className="ms-3">
            <Dropdown.Toggle
              variant="primary"
              className="profile-toggle d-flex align-items-center gap-2 px-3 py-1 rounded-pill"
            >
              <div className="avatar-circle">{firstInitial}</div>
              <span className="fw-bold text-capitalize">{user?.firstName}</span>
            </Dropdown.Toggle>

            <Dropdown.Menu className="dropdown-fade">
              <Dropdown.Item
                href="/admin/profile"
                className="dropdown-item-custom"
              >
                <FaUser className="me-2 text-purple" />
                Profile
              </Dropdown.Item>
              <Dropdown.Item
                onClick={handleLogout}
                className="dropdown-item-custom text-danger"
              >
                <FaSignOutAlt className="me-2" />
                Logout
              </Dropdown.Item>
              <Dropdown.Divider />
           
            </Dropdown.Menu>
          </Dropdown>
        </Navbar.Collapse>
      </Container>
    </Navbar>
  );
};

export default AdminNavbar;
