import React, { useEffect, useState } from "react";
import axios from "axios";
import { Table, Button, Alert, Modal, Form } from "react-bootstrap";
import AdminNavbar from "../components/AdminNavbar";


const PendingApproval = () => {
  const [pendingUsers, setPendingUsers] = useState([]);
  const [selectedUser, setSelectedUser] = useState(null);
  const [showModal, setShowModal] = useState(false);
  const [role, setRole] = useState("");
  const [message, setMessage] = useState("");
  const [alert, setAlert] = useState("");
  const [users, setUsers] = useState([]);

  // Fetch pending users
  useEffect(() => {
    fetchPendingUsers();
  }, []);

  const fetchPendingUsers = async () => {
    try {
      const res = await axios.get("/api/users/pending");
      setPendingUsers(res.data);
    } catch (err) {
      console.error("Error fetching pending users:", err);
    }
  };

  const handleApproveClick = (user) => {
    setSelectedUser(user);
    setShowModal(true);
  };

  const handleApprove = async () => {
    if (!role) {
      alert("Please select a role before approving.");
      return;
    }
  
    try {
      await axios.patch(
        `/api/users/${selectedUser._id}/approve`,
        {
          role,
          message: message || "You have been approved by the admin.",
        },
        {
          headers: {
            Authorization: `Bearer ${localStorage.getItem("token")}`,
          },
        }
      );
  
      setAlert({
        type: "success",
        message: `‚úÖ ${selectedUser.firstName} approved successfully`,
      });
  
      setShowModal(false);           // Close modal
      fetchPendingUsers();           // Refresh list
    } catch (err) {
      console.error("‚ùå Error approving user:", err);
      setAlert({
        type: "danger",
        message: "‚ùå Approval failed. Check server logs.",
      });
    }
  };
  

  const handleReject = async (userId) => {
    try {
      await axios.patch(`/api/users/${userId}/reject`, {
        message: "We're sorry, your registration request was rejected.",
      }, {
        headers: {
          Authorization: `Bearer ${localStorage.getItem("token")}`,
        },
      });
  
      setAlert({
        type: "success",
        message: "User rejected and deleted successfully.",
      });
      setTimeout(() => {
        setAlert(null); // Clear the alert after 3 seconds
      }, 3000);
      setUsers(users.filter((user) => user._id !== userId));
    } catch (error) {
      console.error("‚ùå Error rejecting user:", error);
      setAlert({ type: "danger", message: "Rejection failed." });
    }
  };
  

  return (
    
    <>
    <AdminNavbar /> {/* ‚úÖ render navbar here */}
   
    <div className="container mt-4">
      <h2>üö¶ Pending Approvals</h2>

      {alert && (
        <div className={`alert alert-${alert.type}`} role="alert">
          {alert.message}
        </div>
      )}

      <Table striped bordered hover responsive className="mt-3">
        <thead>
          <tr>
            <th>Name</th>
            <th>Email</th>
            <th>Phone</th>
            <th>Address</th>
            <th>Company</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {pendingUsers.length === 0 ? (
            <tr>
              <td colSpan="6" className="text-center text-muted">
                No pending users.
              </td>
            </tr>
          ) : (
            pendingUsers.map((user) => (
              <tr key={user._id}>
                <td>
                  {user.firstName} {user.lastName}
                </td>
                <td>{user.email}</td>
                <td>{user.phone}</td>
                <td>{user.address}</td>
                <td>{user.companyName || "‚Äî"}</td>
                <td>
                  <Button
                    variant="success"
                    size="sm"
                    onClick={() => handleApproveClick(user)}
                    className="me-2"
                  >
                    Approve
                  </Button>
                  <Button
                    variant="danger"
                    size="sm"
                    onClick={() => handleReject(user._id)}
                  >
                    Reject
                  </Button>
                </td>
              </tr>
            ))
          )}
        </tbody>
      </Table>

      {/* Approval Modal */}
      <Modal show={showModal} onHide={() => setShowModal(false)}>
        <Modal.Header closeButton>
          <Modal.Title>Approve User</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <p>
            Approving <strong>{selectedUser?.firstName}</strong>. Select a role
            and optionally add a message.
          </p>
          <Form.Group className="mb-3">
            <Form.Label>Select Role</Form.Label>
            <Form.Select value={role} onChange={(e) => setRole(e.target.value)}>
              <option value="">-- Choose Role --</option>
              <option value="admin">Admin</option>
              <option value="staff">Staff</option>
              <option value="external_company">External Company</option>
            </Form.Select>
          </Form.Group>
          <Form.Group>
            <Form.Label>Optional Message</Form.Label>
            <Form.Control
              as="textarea"
              rows={3}
              value={message}
              onChange={(e) => setMessage(e.target.value)}
              placeholder="Your account has been approved. Welcome!"
            />
          </Form.Group>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={() => setShowModal(false)}>
            Cancel
          </Button>
          <Button variant="primary" onClick={handleApprove}>
            Approve
          </Button>
        </Modal.Footer>
      </Modal>
    </div>
    </>
  ); 
};

export default PendingApproval;
