// frontend/src/components/PendingApproval.js
import React, { useEffect, useState } from "react";
import axios from "axios";
import { Table, Button, Alert, Dropdown, DropdownButton } from "react-bootstrap";

const PendingApproval = () => {
  const [users, setUsers] = useState([]);
  const [error, setError] = useState("");
  const [success, setSuccess] = useState("");
  const [roleSelections, setRoleSelections] = useState({});

  useEffect(() => {
    fetchUsers();
  }, []);

  const fetchUsers = async () => {
    try {
      const res = await axios.get("/api/users");
      const pendingUsers = res.data.filter((u) => u.approved === false);
      setUsers(pendingUsers);
    } catch (err) {
      console.error("Error fetching users:", err);
    }
  };

  const handleApprove = async (userId, email) => {
    const role = roleSelections[userId];
    if (!role) return alert("Please select a role before approving.");

    try {
      await axios.patch(
        `/api/users/${userId}/approve`,
        { role },
        {
          headers: {
            Authorization: `Bearer ${localStorage.getItem("token")}`,
          },
        }
      );
      setSuccess("User approved and email sent.");
      setTimeout(() => setSuccess(""), 3000);
      fetchUsers();
    } catch (err) {
      console.error("Approval error:", err);
      setError("Approval failed.");
      setTimeout(() => setError(""), 3000);
    }
  };

  const handleReject = async (userId, email) => {
    try {
      await axios.patch(
        `/api/users/${userId}/reject`,
        {},
        {
          headers: {
            Authorization: `Bearer ${localStorage.getItem("token")}`,
          },
        }
      );
      setSuccess("User rejected and email sent.");
      setTimeout(() => setSuccess(""), 3000);
      fetchUsers();
    } catch (err) {
      console.error("Rejection error:", err);
      setError("Rejection failed.");
      setTimeout(() => setError(""), 3000);
    }
  };

  return (
    <div className="container mt-4">
      <h2>ðŸš¦ Pending Approvals</h2>
      {error && <Alert variant="danger">{error}</Alert>}
      {success && <Alert variant="success">{success}</Alert>}

      <Table striped bordered hover>
        <thead>
          <tr>
            <th>Email</th>
            <th>Phone</th>
            <th>Address</th>
            <th>Company</th>
            <th>Role</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {users.map((user) => (
            <tr key={user._id}>
              <td>{user.email}</td>
              <td>{user.phone}</td>
              <td>{user.address}</td>
              <td>{user.companyName || "â€”"}</td>
              <td>
                <DropdownButton
                  variant="secondary"
                  title={roleSelections[user._id] || "Select"}
                  onSelect={(role) =>
                    setRoleSelections((prev) => ({ ...prev, [user._id]: role }))
                  }
                >
                  <Dropdown.Item eventKey="admin">Admin</Dropdown.Item>
                  <Dropdown.Item eventKey="staff">Staff</Dropdown.Item>
                  <Dropdown.Item eventKey="external_company">External Company</Dropdown.Item>
                </DropdownButton>
              </td>
              <td>
                <Button
                  variant="success"
                  className="me-2"
                  onClick={() => handleApprove(user._id, user.email)}
                >
                  Approve
                </Button>
                <Button
                  variant="danger"
                  onClick={() => handleReject(user._id, user.email)}
                >
                  Reject
                </Button>
              </td>
            </tr>
          ))}
        </tbody>
      </Table>
    </div>
  );
};

export default PendingApproval;
