// src/components/RejectUserModal.js
import React, { useState } from "react";
import { Modal, Button, Form } from "react-bootstrap";

const RejectUserModal = ({ show, onHide, onReject, user }) => {
  const [message, setMessage] = useState("");

  const handleSubmit = () => {
    onReject(user._id, message);
    onHide();
  };

  return (
    <Modal show={show} onHide={onHide}>
      <Modal.Header closeButton>
        <Modal.Title>Rejecting {user?.firstName}</Modal.Title>
      </Modal.Header>
      <Modal.Body>
        <Form>
          <Form.Group>
            <Form.Label>Optional Message</Form.Label>
            <Form.Control
              as="textarea"
              rows={3}
              placeholder="Write a reason or feedback..."
              value={message}
              onChange={(e) => setMessage(e.target.value)}
            />
          </Form.Group>
        </Form>
      </Modal.Body>
      <Modal.Footer>
        <Button variant="secondary" onClick={onHide}>Cancel</Button>
        <Button variant="danger" onClick={handleSubmit}>Reject</Button>
      </Modal.Footer>
    </Modal>
  );
};

export default RejectUserModal;
