import React, { useState } from "react";
import { Button, Container, Row, Col, Modal } from "react-bootstrap";
import { useNavigate } from "react-router-dom";
import Signup from "./Signup"; // we'll reuse the Signup form inside modal

const Home = () => {
  const navigate = useNavigate();
  const [showSignup, setShowSignup] = useState(false);

  return (
    <Container className="text-center mt-5">
      <Row className="justify-content-center">
        <Col md={8}>
          <div className="p-4 rounded shadow-sm bg-light">
            <h1 className="mb-4">Welcome to Kings Property Portal üè¢</h1>
            <p className="lead mb-4">
              Manage your properties, assets, maintenance, and teams ‚Äî all in one platform.
            </p>
            <div className="d-flex justify-content-center gap-3">
              <Button variant="primary" onClick={() => navigate("/login")}>
                Login
              </Button>
              <Button variant="success" onClick={() => setShowSignup(true)}>
                Sign Up
              </Button>
            </div>
          </div>
        </Col>
      </Row>

      {/* üîΩ Signup Modal */}
      <Modal show={showSignup} onHide={() => setShowSignup(false)}>
        <Modal.Header closeButton>
          <Modal.Title>Create Your Account</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Signup onSuccess={() => setShowSignup(false)} />
        </Modal.Body>
      </Modal>
    </Container>
  );
};

export default Home;
